using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Text.RegularExpressions;
using System.Threading.Tasks;

namespace AdventOfCode2017.Milliseconds
{
    public static class One
    {
        public static void Part2()
        {
            string input = "";

            //string input = "1212";
            //string input = "1221";
            //string input = "123425";
            //string input = "123123";
            //string input = "12131415";
            int half = input.Length / 2;
            string firstHalf = input.Substring(0, half);
            string secondHalf = input.Substring(half);

            input = "";
            for (int i = 0; i < half; i++)
            {
                input += $"{firstHalf[i]}{secondHalf[i]}";
            }
            input += input;
            Console.WriteLine(input);

            int sum = 0;
            while (input.Length > 0)
            {
                if (input[0] == input[1])
                    sum += int.Parse(input[0].ToString());
                input = input.Substring(2);
            }

            Console.WriteLine($"Solution to CAPTCHA: {sum}");

        }

        public static void Part1()
        {
            string input = "";

            //string input = "1122";
            //string input = "1111";
            //string input = "1234";
            //string input = "91212129";

            int sum = 0;
            Regex regex = new Regex("(1+|2+|3+|4+|5+|6+|7+|8+|9+|0+)");
            var matches = regex.Matches(input);
            foreach (Match match in matches)
            {
                sum += int.Parse(match.Value[0].ToString()) * (match.Value.Length - 1);
            }
            if (input[0] == input.Last())
            {
                sum += int.Parse(input[0].ToString());
            }

            Console.WriteLine($"Solution to CAPTCHA: {sum}");

        }
    }
}
